import React, { useState } from "react";

function Hero() {
  const [city, setCity] = useState("");        // for user input
  const [weather, setWeather] = useState(null); // to store fetched weather data
  const [error, setError] = useState("");       // for error handling

  const API_KEY = "50a5754614d571948ae380c6fbb5c9d1"; // ðŸ‘ˆ replace this with your real OpenWeather API key

  const fetchWeather = async () => {
        if (!city) {  //   ! !  no city
         setError("Please enter a city name");
        return <p> Location is not present  </p> ;
    }
    try {
      setError(""); // clear previous error
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );

        const data = await response.json();
        if (response.ok) {
         setWeather(data);
        } else {
         setWeather(null);
         setError("City not found");
        }
    } catch (err) {
      setError("Error fetching data");
      setWeather(null);
    }

  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Weather Data</h1>

      <div style={styles.inputContainer}>
        <input
          type="text"
          placeholder="Search Location"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWeather} style={styles.button}>
          Get Weather
        </button>
      </div>

      {error && <p style={styles.error}>{error}</p>}
      {weather && (
        <div style={styles.result}>
          <h2>City: {weather.name}</h2>
          <p>Temperature: {weather.main.temp} Â°C</p>
          <p>Wind Speed: {weather.wind.speed} m/s</p>
          <p>Weather: {weather.weather[0].description}</p>
        </div>
      )}
 {/* <p>Welcome, {!city}</p> */}
      <p style={styles.footer}>Copyright @ Imax</p>
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: "#c9baa6",
    borderRadius: "20px",
    padding: "40px",
    maxWidth: "600px",
    margin: "50px auto",
    textAlign: "center",
    boxShadow: "0px 4px 10px rgba(0,0,0,0.2)",
  },
  title: {
    color: "#0d2c4b",
    textDecoration: "underline",
  },
  inputContainer: {
    margin: "20px 0",
  },
  input: {
    padding: "10px",
    width: "250px",
    borderRadius: "15px",
    border: "2px solid #000",
    marginRight: "10px",
  },
  button: {
    padding: "10px 15px 22px 15px",
    borderRadius: "15px",
    border: "2px solid #000",
    backgroundColor: "#b5d3e7",
    cursor: "pointer",
  },
  result: {
    marginTop: "20px",
  },
  error: {
    color: "red",
  },
  footer: {
    marginTop: "30px",
    color: "#9e9e9e",
  },
};
export default Hero;
