import React, { useState } from "react";

function Hero() {
  const [city, setCity] = useState("");
  const [forecast, setForecast] = useState([]);
  const [error, setError] = useState("");

  const apiKey = "9b93d18187994bb82e265588def39487";
  const cnt = 7; // Number of days to fetch

  // Fetch forecast by coordinates (latitude & longitude)
  const getForecastByCoords = async (lat, lon) => {
    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/forecast/daily?lat=${lat}&lon=${lon}&cnt=${cnt}&appid=${apiKey}&units=metric`
      );
      const data = await response.json();

      if (data.cod === "200" || data.cod === 200) {
        setForecast(data.list); // daily forecast array
        setError("");
      } else {
        setError(data.message);
        setForecast([]);
      }
    } catch (err) {
      console.error(err);
      setError("Failed to fetch forecast data");
      setForecast([]);
    }
  };

  // Get coordinates from city name (geocoding)
  const getForecastByCity = async () => {
    if (!city) {
      setError("Please enter a city name");
      setForecast([]);
      return;
    }

    try {
      // Step 1: Get lat & lon using city name
      const geoRes = await fetch(
        `https://api.openweathermap.org/geo/1.0/direct?q=${city}&limit=1&appid=${apiKey}`
      );
      const geoData = await geoRes.json();

      if (!geoData[0]) {
        setError("City not found");
        setForecast([]);
        return;
      }

      const { lat, lon } = geoData[0];

      // Step 2: Fetch daily forecast
      getForecastByCoords(lat, lon);
    } catch (err) {
      console.error(err);
      setError("Failed to fetch forecast data");
      setForecast([]);
    }
  };

  // Current location button
  const handleCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const lat = position.coords.latitude;
          const lon = position.coords.longitude;
          getForecastByCoords(lat, lon);
        },
        (err) => {
          console.error(err);
          setError("Unable to get your location");
        }
      );
    } else {
      setError("Geolocation is not supported by your browser");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <input
        type="text"
        placeholder="Search Location"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{
          padding: "10px",
          width: "250px",
          borderRadius: "5px",
          border: "1px solid #ccc",
          marginRight: "10px",
        }}
      />
      <button
        onClick={getForecastByCity}
        style={{
          padding: "10px 20px",
          borderRadius: "5px",
          border: "none",
          backgroundColor: "#007bff",
          color: "#fff",
          cursor: "pointer",
          marginRight: "10px",
        }}
      >
        Get Forecast
      </button>
      <button
        onClick={handleCurrentLocation}
        style={{
          padding: "10px 20px",
          borderRadius: "5px",
          border: "none",
          backgroundColor: "#28a745",
          color: "#fff",
          cursor: "pointer",
        }}
      >
        Current Location
      </button>

      {error && <p style={{ color: "red", marginTop: "20px" }}>{error}</p>}

      {forecast.length > 0 && (
        <div style={{ marginTop: "30px" }}>
          <h2>7-Day Forecast</h2>
          {forecast.map((day, index) => (
            <div key={index} style={{ marginBottom: "15px" }}>
              <h4>Day {index + 1}</h4>
              <p>Temperature: {day.temp.day} °C</p>
              <p>Weather: {day.weather[0].description}</p>
              <p>Wind Speed: {day.speed} m/s</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Hero;
