import React, { useState } from "react";

function Hero() {
  const [city, setCity] = useState("");          // User input
  const [weather, setWeather] = useState(null);  // API data
  const [error, setError] = useState("");        // Error messages

  // Using your API key directly
  const apiKey = "ac88b05887e90eef5ff79533f4c2869d";

  // Fetch weather data function
  const getWeather = async () => {
    if (!city) {
      setError("Please enter a city name");
      setWeather(null);
      return;
    }

    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`
      );
      const data = await response.json();

      if (data.cod === 200) {
        setWeather(data);
        setError("");
      } else {
        setError(data.message);
        setWeather(null);
      }
    } catch (err) {
      console.error("Error fetching weather:", err);
      setError("Failed to fetch weather data");
      setWeather(null);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <input
        type="text"
        placeholder="Search Location"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{
          padding: "10px",
          width: "250px",
          borderRadius: "5px",
          border: "1px solid #ccc",
          marginRight: "10px",
        }}
      />
      <button
        onClick={getWeather}
        style={{
          padding: "10px 20px",
          borderRadius: "5px",
          border: "none",
          backgroundColor: "#007bff",
          color: "#fff",
          cursor: "pointer",
        }}
      >
        Get Weather
      </button>

      {error && <p style={{ color: "red", marginTop: "20px" }}>{error}</p>}

      {weather && (
        <div style={{ marginTop: "30px" }}>
          <h2>City: {weather.name}</h2>
          <h4>Temperature: {weather.main.temp} Â°C</h4>
          <h4>Wind Speed: {weather.wind.speed} m/s</h4>
          <h4>Weather: {weather.weather[0].description}</h4>
        </div>
      )}
    </div>
  );
}

export default Hero;
