import React, { useState, useEffect } from "react";
// import "./Hero.css";

function Hero() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const API_KEY = "50a5754614d571948ae380c6fbb5c9d1";

  // Function to fetch weather data
  const fetchWeather = async () => {
    if (!city) return;
    try {
      setError("");
      const res = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      const data = await res.json();

      if (res.ok) {
        setWeather(data);
      } else {
        setWeather(null);
        setError("City not found");
      }
    } catch {
      setError("Error fetching data");
      setWeather(null);
    }
  };

  // ðŸ‘‡ useEffect runs automatically when 'city' changes
  useEffect(() => {
    if (city.trim() !== "") {
      const timer = setTimeout(() => {
        fetchWeather();
      }, 800); // delay for better performance (debounce effect)
      return () => clearTimeout(timer);
    }
  }, [city]); // dependency array

  return (
    <div id="allmain">
      <h1>Weather Data</h1>

      <div>
        <input
          type="text"
          placeholder="Search Location"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        {/* We can keep button as optional trigger */}
        <button onClick={fetchWeather}>Get Weather</button>
      </div>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {weather && (
        <div>
          <h2>{weather.name}</h2>
          <h4>Temperature: {weather.main.temp}Â°C</h4>
          <h4>Wind Speed: {weather.wind.speed} m/s</h4>
          <h4>Weather: {weather.weather[0].description}</h4>
        </div>
      )}

      <p>Â© Imax</p>
    </div>
  );
}

export default Hero;
