import React, { useState } from "react";

function Hero() {
  const [city, setCity] = useState("");          // For user input
  const [weather, setWeather] = useState(null);  // For storing API data
  const [error, setError] = useState("");        // For API error handling

  const apiKey = process.env.REACT_APP_WEATHER_API_KEY; // From .env file

  // Function to fetch weather data
  const getWeather = async () => {
    if (!city) {
      setError("Please enter a city name");
      return;
    }

    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`
      );

      const data = await response.json();

      if (data.cod === 200) {
        setWeather(data);
        setError("");
      } else {
        setError(data.message);
        setWeather(null);
      }
    } catch (err) {
      console.error("Error fetching weather:", err);
      setError("Failed to fetch data");
    }
  };

  return (
    <>
      <input
        type="text"
        placeholder="Search Location"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <button onClick={getWeather}>Get Weather</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {weather && (
        <>
          <h2>City: {weather.name}</h2>
          <h4>Temperature: {weather.main.temp} Â°C</h4>
          <h4>Wind Speed: {weather.wind.speed} m/s</h4>
          <h4>Weather: {weather.weather[0].description}</h4>
        </>
      )}
    </>
  );
}

export default Hero;
