import React, { useState } from "react";
import Message from "./hero";

function Header() {
  const [count, setCount] = useState(0);

  // Function to pass to child
  const increment = () => setCount(count + 1);
  const reset = () => setCount(0);

  return (
    <div>
      <h1>Parent Controlled Child Example</h1>
      <p>Count: {count}</p>

      {/* Child buttons controlled by parent */}
      <Message text="Increment" onClick={increment} user={{name: "Kanwar", role: "Developer"}} />
      <Message text="Reset" onClick={reset} user={{name: "John", role: "Designer"}} />
    </div>
  );
}

export default Header;
