import React, { useState } from "react";
import "./Hero.css";

function Hero() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  const API_KEY = "YOUR_API_KEY"; // üîë Replace with your OpenWeatherMap API key

  // Fetch weather data from OpenWeatherMap
  const fetchWeather = async () => {
    if (!city) return;

    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      const data = await response.json();

      if (data.cod === "404") {
        setError("City not found");
        setWeather(null);
      } else {
        setWeather(data);
        setError(null);
      }
    } catch (err) {
      setError("Failed to fetch weather data");
      setWeather(null);
    }
  };

  // Trigger fetch on Enter key
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      fetchWeather();
    }
  };

  return (
    <div className="hero-container">
      <input
        type="text"
        placeholder="Enter City (e.g. Delhi)"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        onKeyDown={handleKeyPress}
        className="search-box"
      />
      <button onClick={fetchWeather}>Get Weather</button>

      {error && <div className="error">{error}</div>}

      {weather && !error && (
        <div className="weather-info">
          <h2>üåÜ City: {weather?.name}</h2>
          <h3>üå°Ô∏è Temperature: {weather?.main?.temp}¬∞C</h3>
          <h4>üí® Wind Speed: {weather?.wind?.speed} m/s</h4>
          <h4>‚òÅÔ∏è Condition: {weather?.weather?.[0]?.description}</h4>
        </div>
      )}
    </div>
  );
}

export default Hero;
