import React, { useState, useEffect } from "react";
// import "./Hero.css";

function Hero() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const API_KEY = "50a5754614d571948ae380c6fbb5c9d1";

  // âœ… useEffect: runs every time city changes
  useEffect(() => {
    if (city.trim() !== "") {
      console.log("Typed City:", city); // show typed value
    }
  }, [city]);

  // âœ… fetch weather when button clicked
  const fetchWeather = async () => {
    if (!city) return setError("Please enter a city name");

    try {
      setError("");
      const res = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      const data = await res.json();

      if (res.ok) {
        setWeather(data);
        console.log("Fetched Weather Data:", data); // show API data in console
      } else {
        setWeather(null);
        setError("City not found");
      }
    } catch {
      setError("Error fetching data");
      setWeather(null);
    }
  };

  return (
    <div id="allmain">
      <h1>Weather Data</h1>

      <div>
        <input
          type="text"
          placeholder="Search Location"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button onClick={fetchWeather}>Get Weather</button>
      </div>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {weather && (
        <div>
          <h3>City: {weather.name}</h3>
          <h4>Wind Speed: {weather.wind.speed} m/s</h4>
          <h4>Temperature: {weather.main.temp}Â°C</h4>
        </div>
      )}
    </div>
  );
}
export default Hero;
