// I would use a state variable to store input, and another array state to store all values. On button click, I‚Äôll add the input to that array using the spread operator. Then React will automatically re-render the list.‚Äù
import React, { useState } from "react";

function Wishlist() {
  const [item, setItem] = useState([]); // stores all items

  let currentInput = ""; // temporary variable for input

  const handleChange = (e) => {
    currentInput = e.target.value; // update temporary input
  };

  const addItem = () => {
    if (currentInput.trim() !== "") {
      setItem([...item, currentInput]); // add input to array
      currentInput = ""; // clear temporary input
      document.getElementById("inputBox").value = ""; // clear input field
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>üõí Simple Wishlist</h2>
      <input
        type="text"
        id="inputBox"
        placeholder="Enter item..."
        onChange={handleChange}
      />
      <button onClick={addItem}>Add Item</button>

      <ul >
        {item.map((wish, index) => (
          <li>‚≠ê {wish} </li>
        ))}
      </ul>
    </div>
  );
}

export default Wishlist;



