// src/component/hero.js
import React, { useState } from "react";
import axios from "axios";

function Hero() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => setCity(e.target.value);

  const getWeather = async () => {
    const query = city.trim();
    if (!query) {
      setError("Please enter a city name.");
      setWeather(null);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Put your API key into an environment variable (recommended).
      const apiKey = process.env.REACT_APP_OWM_API_KEY || "c8420eb569b698174b96b2167c39f10e";
      const q = encodeURIComponent(query);                // safe encode
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${q}&appid=${apiKey}&units=metric`;

      const response = await axios.get(url);
      console.log("Weather API response:", response.data); // debug: network & payload
      setWeather(response.data);
    } catch (err) {
      console.error("Weather API error (full):", err);

      // Prefer descriptive server message if available
      if (err.response && err.response.data) {
        // openweathermap returns { cod: "404", message: "city not found" } for missing city
        const serverMsg = err.response.data.message || `HTTP ${err.response.status}`;
        setError(serverMsg);
      } else if (err.request) {
        // Request was made but no response (possible CORS or network)
        setError("No response from server. Check network or CORS (see console/network tab).");
      } else {
        setError(err.message);
      }
      setWeather(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="hero">
      <input
        type="text"
        placeholder="Search Location (e.g. New Delhi or Delhi,IN)"
        value={city}
        onChange={handleChange}
      />
      <button onClick={getWeather} disabled={loading}>
        {loading ? "Loading..." : "Get Weather"}
      </button>

      {error && <p style={{ color: "crimson", marginTop: 12 }}>{error}</p>}

      {weather && (
        <div style={{ marginTop: 12 }}>
          <h2>City: {weather.name}{weather.sys?.country ? `, ${weather.sys.country}` : ""}</h2>
          <h4>Temperature: {weather.main?.temp} Â°C</h4>
          <h4>Wind Speed: {weather.wind?.speed} m/s</h4>
          <h4>Condition: {weather.weather?.[0]?.description}</h4>
        </div>
      )}
    </div>
  );
}

export default Hero;
