import React, { useState } from "react";

function Hero() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const API_KEY = "50a5754614d571948ae380c6fbb5c9d1";

  const fetchWeather = async () => {
    if (!city) return setError("Please enter a city name");

    try {
      setError("");
      const res = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      const data = await res.json();

      if (res.ok) {
        setWeather(data);
      } else {
        setWeather(null);
        setError("City not found");
      }
    } catch {
      setError("Error fetching data");
      setWeather(null);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Weather Data</h1>

      <div style={styles.inputContainer}>
        <input
          type="text"
          placeholder="Search Location"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWeather} style={styles.button}>
          Get Weather
        </button>
      </div>

      {error && <p style={styles.error}>{error}</p>}
      {weather && (
        <div style={styles.result}>
          <h2>{weather.name}</h2>
          <p>Temperature: {weather.main.temp}°C</p>
          <p>Wind: {weather.wind.speed} m/s</p>
          <p>{weather.weather[0].description}</p>
        </div>
      )}

      <p style={styles.footer}>© Imax</p>
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: "#c9baa6",
    borderRadius: 20,
    padding: 40,
    maxWidth: 600,
    margin: "50px auto",
    textAlign: "center",
    boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
  },
  title: { color: "#0d2c4b", textDecoration: "underline" },
  inputContainer: { margin: "20px 0" },
  input: {
    padding: 10,
    width: 250,
    borderRadius: 15,
    border: "2px solid #000",
    marginRight: 10,
  },
  button: {
    padding: "10px 15px",
    borderRadius: 15,
    border: "2px solid #000",
    backgroundColor: "#b5d3e7",
    cursor: "pointer",
  },
  result: { marginTop: 20 },
  error: { color: "red" },
  footer: { marginTop: 30, color: "#777" },
};

export default Hero;
